class Solution {public:    void setZeroes(vector<vector<int>>& matrix) {        bool s1 = false;        bool s2 = false;        for (unsigned i = 0; i < matrix.size(); ++i) {            for (unsigned j = 0; j < matrix[0].size(); ++j) {                if (matrix[i][j] == 0) {                    matrix[i][0] = 0;                    matrix[0][j] = 0;                    if (i == 0) s1 = true;                    if (j == 0) s2 = true;                }            }        }        for (unsigned i = 1; i < matrix.size(); ++i) {            if (matrix[i][0] == 0) {                for (unsigned j = 1; j < matrix[0].size(); ++j) {                    matrix[i][j] = 0;                }            }        }        for (unsigned i = 1; i < matrix[0].size(); ++i) {            if (matrix[0][i] == 0) {                for (unsigned j = 1; j < matrix.size(); ++j) {                    matrix[j][i] = 0;                }            }        }        if (s1) {            for (unsigned i = 1; i < matrix[0].size(); ++i) {                matrix[0][i] = 0;            }        }        if (s2) {            for (unsigned i = 1; i < matrix.size(); ++i) {                matrix[i][0] = 0;            }        }    }};